import axios, { AxiosResponse } from "axios";
import { ChangeEvent, useState } from "react";
import { Form, Button } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { IBookmark } from "../../models/models";
import { ActionType } from "../../redux/action-type";

let AddBookmarkModal = (): JSX.Element => {

    // let history = useHistory();
    let dispatch = useDispatch();
    
        let [name, setName] = useState("");
        let [url, setUrl] = useState("");
        let [category, setCategory] = useState("");

        // let [image, setImage] = useState("");
    
        let newBookmark = {
            name: name,
            url: url,
            category: category
        }
    
        let onNameTyped = (event: ChangeEvent<HTMLInputElement>) => {
            setName(event.target.value)
        }
        let onUrlTyped = (event: ChangeEvent<HTMLInputElement>) => {
            setUrl(event.target.value);
        }
        let onCategoryTyped = (event: ChangeEvent<HTMLInputElement>) => {
            setCategory(event.target.value);
        }
    
        function onAddBookmarkClicked() {
            try {
                let response = axios.post<AxiosResponse<IBookmark>>("http://localhost:5000/bookmarks/addBookmark/", newBookmark);
                console.log(response);
                dispatch({ type: ActionType.AddBookmark, payload: newBookmark });
    
            }
            catch (e) {
                console.log(e);
            }
            alert("Bookmark successfully added!");
        }
    
    
        return (
            <div className="AddVacationModal">
                <br />
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Bookmark name:</Form.Label>
                    <Form.Control type="text" className="form" onChange={onNameTyped} /> <br />
                    <Form.Label>URL:</Form.Label>
                    <br />
                    <Form.Control type="text" className="form" onChange={onUrlTyped} /> <br />
                    <Form.Label>Category:</Form.Label>
                    <Form.Control type="text" className="form" onChange={onCategoryTyped} /><br />
    
                 
                    <Button variant="primary" className="AddVacationButton" size="lg" onClick={onAddBookmarkClicked} >Add new Bookmark</Button>
                </Form.Group>
            </div>
        )
    
    }
    
    export default AddBookmarkModal;
