import React, { StrictMode, useEffect, useState } from 'react'
import './Main.css'
import axios from 'axios'
import { useDispatch, useSelector } from 'react-redux';
import { AppState } from '../../redux/app-state';
import { ActionType } from '../../redux/action-type';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import BookmarkCard from '../bookmarkCard/bookmarkCard';
import AddBookmarkModal from '../addBookmarkModal/AddBookmarkModal';
import { useNavigate } from 'react-router-dom';


function Main() {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const bookmarksArray = useSelector((state: AppState) => state.bookmarksArray);

    let dispatch = useDispatch();
    let navigate = useNavigate();

    const GetUserBookmarks = () => {
        axios.get("http://localhost:5000/bookmarks/")
            .then((response) => {
                let myBookmarks = response.data;
                console.log(myBookmarks);
                dispatch({ type: ActionType.GetAllBookmarks, payload: { myBookmarks } })
            })
    }


    useEffect(() => {
        GetUserBookmarks();
    }
        , [])

    return (
        <StrictMode>
            <div className="main">

                <div className="newBookmarkButtonDiv">
                    <Button variant="success" size="lg" onClick={handleShow}>
                        Add new bookmark!
                    </Button>

                    <Modal show={show} onHide={handleClose} animation={true}>
                        <Modal.Header closeButton>
                            <Modal.Title>Add new Bookmark</Modal.Title>
                        </Modal.Header>
                        <Modal.Body><AddBookmarkModal /></Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>

                        </Modal.Footer>
                    </Modal>
                </div>
                <div className="bookmarksDiv">
                    {bookmarksArray.map((bookmark, index) => (

                        <BookmarkCard key={index} _id={bookmark.id} name={bookmark.name} url={bookmark.url} category={bookmark.category} />

                    ))}
                </div>
            </div>
        </StrictMode>
    )
}

export default Main;
