import React from 'react'
import axios, { AxiosResponse } from 'axios';
import '../Register/Register.css'
import { ChangeEvent, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ILogin, IUser } from '../../models/models';
import "bootstrap/dist/css/bootstrap.min.css";


let Register = (): JSX.Element => {

    const navigate = useNavigate();

    let [username, setUsername] = useState("");
    let [password, setPassword] = useState("");


    let newUser = {
        username: username,
        password: password,
    }

    let onUsernameTyped = (event: ChangeEvent<HTMLInputElement>) => {
        setUsername(event.target.value)
    }
    let onPasswordTyped = (event: ChangeEvent<HTMLInputElement>) => {
        setPassword(event.target.value);
    }

    function onRegisterClicked() {

        if (!newUser.username) {
            alert("Please type a username to register.");
            return Error;
        }

        if (!newUser.password) {
            alert("Please enter a valid password.");
            return Error;
        }



        try {
            let response = axios.post<ILogin, AxiosResponse<IUser>>("http://localhost:5000/users/", newUser);
            console.log(response);

        }
        catch (e) {
            console.log(e);
        }
        alert("User successfully registered! You may login now!");
        navigate("/login");
    }
    function alreadyRegistered() {
        navigate("/login");
    }

    return (

        <div className="registerDiv">
        <h1>Create a new user!</h1>
<div className="formDiv">
<form>
  <div className="form-field">
    <input type="text" placeholder="Email / Username" onChange={onUsernameTyped} required/>
  </div>
  
  <div className="form-field">
    <input type="password" placeholder="Password" onChange={onPasswordTyped} required/>
    </div>
</form>
</div>
<div className="buttonsDiv">
                    <button className="btn" onClick={onRegisterClicked}>Register now!</button> <br /> <br />
                    <button className="btn" onClick={alreadyRegistered}>New user?</button>
                </div>
</div>
    )
}

export default Register;
